changeSet.sql:
CREATE TABLE IF NOT EXISTS users ( `id` VARCHAR(36) UNIQUE NOT NULL, `first_name` VARCHAR(45) NOT NULL, `last_name` VARCHAR(45) NOT NULL, `email` VARCHAR(45) UNIQUE NOT NULL, `phone_number` VARCHAR(45) UNIQUE NOT NULL, `password` VARCHAR(52) NOT NULL, `role` VARCHAR(45) NOT NULL, `trucks_id` VARCHAR(36) NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS trucks ( `id` VARCHAR(36) UNIQUE NOT NULL, `name` VARCHAR(45) NOT NULL, `registration_number` VARCHAR(45) NOT NULL, `max_weight` DOUBLE NOT NULL, `truck_category` VARCHAR(45) NOT NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS orders ( `id` VARCHAR(36) NOT NULL, `date_pickup` DATETIME NOT NULL, `date_delivery` DATETIME NOT NULL, `voucher_pickup` VARCHAR(45) NULL, `voucher_delivery` VARCHAR(45) NULL, `price` DOUBLE NOT NULL, `truck_category` VARCHAR(45) NOT NULL, `carrier_id` VARCHAR(36) NULL, `shipper_id` VARCHAR(36) NOT NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS status_history ( `id` VARCHAR(36) NOT NULL, `name` VARCHAR(45) NOT NULL, `status_date` DATE NOT NULL, `orders_id` VARCHAR(36) NOT NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS loads ( `id` VARCHAR(36) UNIQUE NOT NULL, `name` VARCHAR(45) NOT NULL, `weight` DOUBLE NOT NULL, `details` VARCHAR(45) NOT NULL, `orders_id` VARCHAR(36) NOT NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS orders_address ( `id` VARCHAR(36) UNIQUE NOT NULL, `address_to` VARCHAR(45) NOT NULL, `address_from` VARCHAR(45) NOT NULL, `orders_id` VARCHAR(36) NOT NULL, PRIMARY KEY (`id`));
